INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (11, 'Epicteto fue un filósofo estoico de origen griego que vivió en el siglo I d.C. Sus enseñanzas se centran en la ética y la filosofía práctica, promoviendo la virtud y la autodisciplina como medios para alcanzar la felicidad y la tranquilidad.', 'Epicteto', 'Frigia', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FEpicteto%20%2FEpiceto.jpeg?alt=media&token=c3b17d59-4347-442d-bb93-b7b5dab3f35d', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (31, 'Charles Darwin fue un naturalista británico nacido en 1809. Es famoso por su teoría de la evolución por selección natural, presentada en su obra "El Origen de las Especies". Su trabajo revolucionó la biología y nuestra comprensión de la diversidad de la vida en la Tierra.', 'Charles', 'Darwin', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FCharles%20Darwin%2FCharles%20Darwin.jpg?alt=media&token=5caf7fa2-48c6-4af6-888a-90465081f19a', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (26, 'Ralph Waldo Emerson fue un escritor y filósofo estadounidense nacido en 1803. Es conocido por sus ensayos trascendentalistas, que promovían la individualidad y la conexión con la naturaleza.', 'Ralph', 'Emerson', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FRalph%20Emerson%2FRalph%20Emerson.jpg?alt=media&token=b566b952-8de7-4661-8e2c-abeaed3d1280', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (30, 'Immanuel Kant fue un filósofo alemán nacido en 1724. Es conocido por su obra "Crítica de la Razón Pura", que revolucionó la filosofía moderna al abordar cuestiones de epistemología y ética. Sus ideas han tenido un profundo impacto en la filosofía occidental.', 'Immanuel', 'Kant', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FImmanuel%20Kant%2Fimmanuel%20kant.jpg?alt=media&token=7d25427f-ddbd-478c-972f-96ba31dd8916', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (9, 'Platón fue un filósofo griego nacido en Atenas en el año 427 a.C. Fue discípulo de Sócrates y maestro de Aristóteles. Es conocido por sus diálogos filosóficos, en los que discute una amplia variedad de temas, desde la política hasta la epistemología.', 'Platon', 'Atenas', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FPlaton%20Atenas%2FPlaton%20Ateneas.jpg?alt=media&token=937cfa20-5641-459c-9610-575b1017b466', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (4, 'H.P. Lovecraft (1890-1937) fue un influyente escritor estadounidense de terror cósmico. Conocido por su estilo literario único y su creación del "Mitos de Cthulhu", Lovecraft exploró el horror existencial y lo desconocido en obras como "El llamado de Cthulhu" y "En las montañas de la locura". Su legado perdura en la literatura de horror y la cultura popular, influyendo en autores, cineastas y artistas. A pesar de su breve vida, su obra sigue inspirando la fascinación por lo insondable y lo inexplicable en el género del terror.', 'Howard', 'Lovecraft', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FLovecraft%2FLovecraft.jpg?alt=media&token=d9e573ed-40f2-49a3-b220-83b15f8f407b', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (10, 'Bram Stoker (1847-1912) fue un escritor irlandés famoso por su novela "Drácula" (1897), que definió el género de vampiros en la literatura y dejó un impacto duradero en la cultura popular. Stoker trabajó como gerente de teatro y escribió numerosas obras antes de crear al icónico Conde Drácula. Su influencia en la literatura de terror es innegable, y su obra maestra sigue siendo una de las novelas más leídas y adaptadas en la historia, influyendo en innumerables obras posteriores relacionadas con vampiros y el género de terror gótico.', 'Bram', 'Stoker', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FBram%20Stoker%2FBram.jpg?alt=media&token=cd2b8050-6a00-45b0-b06d-4c085fd6dd11', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (20, 'Friedrich Nietzsche fue un filósofo alemán nacido en 1844. Es conocido por su crítica a la moral tradicional y su concepto del "superhombre". Sus obras, como "Así habló Zaratustra", han influido en la filosofía y la cultura moderna.', 'Friedrich', 'Nietzsche', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FFriedrich%20Nietzsche%2FNietzsche.jpg?alt=media&token=ed145208-5ad7-4136-ad78-22de9aa2f25d', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (24, '"Jorge Isaacs, colombiano del siglo XIX, es conocido por su novela romántica ""María,"" que captura la belleza y tragedia del amor.",', 'Jorge ', 'Isaacs', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FJorge%20Isaacs%2FIssacs.jpg?alt=media&token=b69d4a62-aa65-4c7b-8cf1-ecc4f798966c', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (6, 'Miguel de Cervantes fue un escritor español nacido en 1547. Es famoso por su obra "Don Quijote de la Mancha", considerada una de las obras más importantes de la literatura mundial.', 'Miguel', 'Cervantes', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FMiguel%20Cervantes%2Fmiguel%20cervantes.jpg?alt=media&token=fc0bde74-4595-41fa-b583-4c4ec000bd1d', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (27, '"Victor Hugo, autor francés del siglo XIX, escribió ""Los Miserables"" y ""El Jorobado de Notre-Dame,"" influyendo en la literatura universal.",', 'Victor', 'Hugo', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FVictor%20Hugo%2FVictor%20Hugo.jpg?alt=media&token=1c28ca6f-8309-4673-8c59-56d287e58e2d', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (25, 'Fiódor Dostoyevski (1821-1881) fue un influyente novelista y filósofo ruso del siglo XIX. Nacido en Moscú, luchó contra la pobreza y la epilepsia durante su vida. Sus obras, como "Crimen y castigo" y "Los hermanos Karamázov," exploran temas profundos de la psicología humana, la moralidad y la existencia. Dostoyevski también sufrió prisión y condena a muerte, experiencia que influyó en su escritura. Su enfoque en la condición humana y la naturaleza del sufrimiento lo convirtió en uno de los autores más importantes de la literatura universal, dejando un legado duradero en la literatura y la filosofía.', 'Fiódor', 'Dostoyevski ', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FFi%C3%B3dor%20Dostoyevski%20%2Ffedor%20dostoievski.jpg?alt=media&token=d4d567a6-200a-447a-ae81-38622a37316f', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (21, 'Alexandre Dumas fue un novelista y dramaturgo francés nacido en 1802. Es conocido por sus aventuras históricas, como "Los Tres Mosqueteros" y "El Conde de Montecristo", que han sido adaptadas en numerosas películas y obras de teatro.', 'Alejandro', 'Dumas', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FAlejandro%20Dumas%2FAlexander%20Dumas.jpg?alt=media&token=97ddaa77-461a-4430-b878-9e5868140d5e', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (1, '"Juan Eugenio Hartzenbusch, dramaturgo y poeta español del siglo XIX, contribuyó al teatro español con obras como ""Los Amantes de Teruel.""",', 'Juan Eugenio ', 'Hartzenbusch ', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FJuan%20Eugenio%20Hartzenbusch%20%2FJuan%20Eugenio%20Hartzenbusch.jpg?alt=media&token=05ec8beb-7a12-4b46-954a-5bf9e436ceb7', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (14, 'Dante Alighieri fue un poeta italiano nacido en Florencia en 1265. Es conocido por su obra maestra "La Divina Comedia", un poema épico que describe su viaje a través del Infierno, el Purgatorio y el Paraíso. Es una de las obras más importantes de la literatura italiana y mundial.', 'Dante', 'Alighieri', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FDante%20Alighieri%2FDante%20Alighieri.jpg?alt=media&token=040b0c5d-7d35-437d-adc1-82fdfb1d6d3b', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (18, '"Gustavo Adolfo Bécquer, poeta español del siglo XIX, es amado por su poesía lírica y las ""Rimas"" que dejaron una huella perdurable.",', 'Gustavo Adolfo', 'Bécquer', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FGustavo%20Adolfo%2FGustavo.jpg?alt=media&token=368dbc60-48ab-4978-be0e-0380b60970ae', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (7, 'Confucio, cuyo nombre completo es Kong Fuzi (o Kongzi), fue un filósofo chino nacido en el año 551 a.C. Sus enseñanzas se centran en la moral, la ética y la conducta personal. Sus pensamientos influyeron en la cultura y la filosofía chinas y siguen siendo relevantes en la actualidad.', 'Confucio', 'Kong', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FConfucio%20Kong%2FConfucius.jpg?alt=media&token=8fe404c9-096b-4bc2-861e-3e7a9e073df0', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (8, 'Séneca fue un filósofo, político y escritor romano nacido en el año 4 a.C. Es conocido por sus obras filosóficas y epistolares, que tratan sobre la ética y la filosofía estoica.', 'Lucio', 'Seneca', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FLucio%20Seneca%2FLucio%20Seneca.jpg?alt=media&token=72e8fd5b-fff4-49c6-866b-48b566396cd0', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (5, 'Sir Arthur Conan Doyle fue un escritor escocés nacido en 1859. Es famoso por su creación del detective Sherlock Holmes. Sus historias de Sherlock Holmes se han convertido en clásicos de la literatura detectivesca.', 'Arthur', 'Conan', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FArthur%20Conan%2FConan%20doyle.jpg?alt=media&token=4668c1eb-13d2-445b-87d6-59cd5b99893b', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (22, '"Edith Wharton, novelista estadounidense del siglo XIX, destacó por su crítica social y estilo elegante. Su obra más famosa, ""La Edad de la Inocencia,"" ganó un Pulitzer.",', 'Edith ', 'Wharton ', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FEdith%20Wharton%20%2FEdith%20wharton.jpg?alt=media&token=08a87cdd-f440-4187-ab75-8f3db8fde7df', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (15, 'Aristóteles, cuyo nombre completo es Aristóteles de Estagira, fue un filósofo griego nacido en Estagira en el año 384 a.C. Fue discípulo de Platón y luego maestro de Alejandro Magno. Sus obras abarcan una amplia gama de temas, desde la ética hasta la política y la biología, y han tenido una influencia duradera en la filosofía occidental.', 'Aristoteles', 'Estagira', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FAristoteles%20Estagira%2FAristoteles.jpg?alt=media&token=40b2a505-41b1-454a-bc65-e980ea6c2ffa', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (28, '"Ramón María del Valle-Inclán, dramaturgo y novelista español del siglo XX, innovó en la literatura con su estilo vanguardista y ""Luces de Bohemia.""",', 'Ramón ', 'María del Valle-Inclán', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FRam%C3%B3n%20Mar%C3%ADa%20del%20Valle-Incl%C3%A1n%2FValle.jpg?alt=media&token=d0a4e7d0-98c1-4258-941c-3ae15e08e1e9', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (29, '"Vicente Riva Palacio, escritor y político mexicano del siglo XIX, destacó con su obra ""México a través de los Siglos"" y su participación en la Revolución Mexicana.",', 'Vicente ', 'Riva Palacio', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FVicente%20Riva%20Palacio%2Friva_palacio.jpg?alt=media&token=ed245be1-bb19-48b1-9944-0a4a62ca7635', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (3, 'Jane Austen (1775-1817) fue una influyente novelista británica del siglo XIX. Su ingenio y aguda observación social la llevaron a escribir obras literarias atemporales, incluyendo "Orgullo y Prejuicio," "Sentido y Sensibilidad" y "Emma." Austen exploró las complejidades de la sociedad, el amor y el matrimonio en la Inglaterra de la Regencia, con un estilo distintivo y satírico. Aunque su obra recibió reconocimiento póstumo, hoy es ampliamente considerada como una de las autoras más destacadas en la literatura clásica y ha dejado un legado perdurable en la literatura mundial.', 'Jane', 'Austen', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FJane%20Austen%2Fjane%20austen.jpg?alt=media&token=206b3dcf-07ba-4b4d-b39c-cc8bf65da0fb', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (13, 'Arthur Schopenhauer fue un filósofo alemán nacido en Danzig (actualmente Gdansk, Polonia) en 1788. Es conocido por su filosofía pesimista y su influencia en la filosofía occidental. Schopenhauer desarrolló una visión pesimista de la vida humana, sosteniendo que el deseo y la voluntad son fuentes de sufrimiento. Su obra más influyente es "El mundo como voluntad y representación", en la que expone su filosofía en detalle.', 'Arthur', 'Schopenhauer', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FArthur%20Schopenhauer%2FArthur%20Schopenhauer.jpg?alt=media&token=da37ca8d-60ce-4115-9b43-7887c840a535', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (17, '"Oscar Wilde, escritor irlandés del siglo XIX, es conocido por sus comedias satíricas como ""El Retrato de Dorian Gray"" y su ingenio inigualable.",', 'Oscar ', 'Wilde', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FOscar%20Wilde%2FOscar%20Wilde.jpg?alt=media&token=1e0ce95c-1c6b-4383-a21d-4f81d1ec0869', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (12, 'Sun Tzu fue un estratega militar chino que vivió en el siglo V a.C. Es conocido por su obra "El Arte de la Guerra", un tratado sobre estrategia militar que ha influido en la estrategia empresarial y militar a lo largo de la historia.', 'Tzu', 'Sun', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FZun%20Tun%2FZun%20Tun.jpg?alt=media&token=8c6b8bd0-224b-4657-9b2f-b6e3b0dfba2d', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (16, 'Robert Louis Stevenson fue un novelista escocés nacido en 1850. Es famoso por obras como "La isla del tesoro" y "El extraño caso del Dr. Jekyll y Mr. Hyde".', 'Robert', 'Stevenson', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FRobert%20Stevenson%2FRobert%20Stevenson.jpg?alt=media&token=c234e7b0-9511-4612-a2e9-9ae8b8f93a70', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (2, 'Edgar Allan Poe fue un escritor estadounidense nacido en 1809. Es famoso por sus historias de terror y misterio, como "El Cuervo" y "El Gato Negro". Su obra ha influido en la literatura de género de horror y detectivesca.', 'Allan', 'Poe', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FAllan%20Poe%2Fpoe_edgar_allan.jpg?alt=media&token=cf6dddb0-93f9-4f4e-bbf2-8cb7202aa57a', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (19, 'El autor anónimo es un término utilizado para obras cuyo autor es desconocido o no se ha revelado. Puede referirse a numerosas obras literarias a lo largo de la historia.', 'Anonimo', 'Anonimo', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FAnonimo%20Anonimo%2Ftaj%20mahal.jpg?alt=media&token=805e74c0-13c0-464f-ae7f-3737d25ebc55', NOW(), NOW(), true);
INSERT INTO author (author_id, about, name, lastname, cover, created_at, modified_at, is_active) VALUES (23, '"Washington Irving, escritor estadounidense del siglo XIX, es famoso por ""La Leyenda de Sleepy Hollow"" y ""Rip Van Winkle,"" contribuyendo a la literatura estadounidense.",', 'Washington ', 'Irving', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Autores%2FWashington%20Irving%2Firving_washington.jpg?alt=media&token=b4ed4a85-0ccb-4811-9eef-2c0c3e87fa12', NOW(), NOW(), true);
INSERT INTO book (book_id, about, author_id, genre, subGenre, name, url, cover, likes, is_active, created_at, modified_at, read_global_time) VALUES (1, '"""Los amantes de Teruel"" de Juan Eugenio Hartzenbusch es una obra teatral basada en una leyenda española del siglo XIII. La historia sigue a Isabel y Diego, dos jóvenes enamorados cuyo amor es obstaculizado por las circunstancias y la enemistad entre sus familias. A pesar de sus esfuerzos por estar juntos, mueren trágicamente sin lograr su unión. La obra explora temas de amor, destino y la lucha contra las adversidades. Esta trágica historia de amor ha perdurado en la cultura española y se ha convertido en un símbolo del amor inquebrantable a lo largo de los siglos."', 1, 'Teatro', 'Drama', 'Los amantes de Teruel', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Libros%2FLos%20amantes%20de%20Teruel%2FLos_amantes_de_Teruel-Juan_Eugenio_Hartzenbusch.epub?alt=media&token=8038d11d-70f1-46cd-a190-840b6ad7f00e', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Libros%2FLos%20amantes%20de%20Teruel%2FLos_amantes_de_Teruel-Juan_Eugenio_Hartzenbusch.jpg?alt=media&token=92d6bf2e-d917-47fb-8d7c-7dc054c6a004', 0, true, NOW(), NOW(), '00:00:00');
