INSERT INTO book (book_id, about, author_id, genre, subGenre, name, url, cover, likes, is_active, created_at, modified_at, read_global_time) VALUES (56, '"Lady Susan de Jane Austen es una novela epistolar que sigue a Lady Susan Vernon, una viuda encantadora y manipuladora que busca matrimonios ventajosos para ella y su hija Frederica. Utilizando su astucia y encanto, Lady Susan navega por las complejidades de la sociedad aristocrática del siglo XIX mientras persigue sus propios intereses. A través de cartas entre los personajes, la historia revela los intrincados juegos de amor y política. Austen, con su agudo ingenio, ofrece una sátira de las convenciones sociales y de género de la época, creando un retrato vívido de una mujer audaz que desafía las expectativas de su tiempo."', 3, 'Novela', 'Romance', 'Lady Susan', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Libros%2FLady%20Susan%2FLady%20Susan%20_%20Los%20Watson%20-%20Jane%20Austen.epub?alt=media&token=c256b84d-33cd-455c-b896-6e9bc7a0f84d', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Libros%2FLady%20Susan%2FLady%20swan.png?alt=media&token=8ca4b5a6-041f-49ea-b539-7aed116c56e9', 0, true, NOW(), NOW(), '00:00:00');
INSERT INTO book (book_id, about, author_id, genre, subGenre, name, url, cover, likes, is_active, created_at, modified_at, read_global_time) VALUES (57, '"Los Watson" de Jane Austen es una novela inacabada que sigue a la familia Watson, encabezada por la inteligente Emma Watson. Cuando el padre de Emma muere, la familia se ve obligada a depender de parientes ricos. La trama se centra en las complejas relaciones y aspiraciones matrimoniales de los personajes, pero la novela permanece incompleta, dejando preguntas sin respuesta. Aunque fragmentaria, "Los Watson" refleja el ingenio y la ironía característicos de Austen, ofreciendo una visión de las tensiones sociales y las expectativas matrimoniales en la Inglaterra del siglo XIX.', 3, 'Novela', 'Romance', 'Los Watson', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Libros%2FLos%20Watson%2FWatson%2C%20Los%20-%20Jane%20Austen.epub?alt=media&token=de6b2b6a-5e1a-4a38-839f-6409e39a335b', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Libros%2FLos%20Watson%2FWatson.png?alt=media&token=1d0f52b2-6f7c-4ebd-b4e3-d92a7e198b4b', 0, true, NOW(), NOW(), '00:00:00');
