INSERT INTO book (book_id, about, author_id, genre, subGenre, name, url, cover, likes, is_active, created_at, modified_at, read_global_time) VALUES (72, '"""Martes de carnaval"" de Ramón María del Valle-Inclán es la segunda entrega de la tetralogía ""Los cuernos de don Friolera."" Esta obra maestra de la literatura española del siglo XX presenta un Madrid surrealista y grotesco a principios del siglo XX. La trama se centra en las desventuras del Marqués de Bradomín mientras se enreda en amores, duelos y conspiraciones políticas. Valle-Inclán utiliza un lenguaje innovador y expresionista para retratar una sociedad decadente y corrupta. La obra es una crítica mordaz a la España de la época y una exploración de las pasiones humanas en un mundo al borde del colapso."', 28, 'Poesia', 'Drama', 'Martes de carnaval', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Libros%2FMartes%20de%20carnaval%2FMartes_de_carnaval-Ram%C3%B3n_del_Valle_Incl%C3%A1n.epub?alt=media&token=12a4dafc-13be-49bf-a9a2-27373da68e22', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Libros%2FMartes%20de%20carnaval%2FMartes_de_carnaval-Ram%C3%B3n_del_Valle_Incl%C3%A1n.jpg?alt=media&token=b8f7a1a1-d9bf-4ec9-b396-652dab9bd273', 0, true, NOW(), NOW(), '00:00:00');
