INSERT INTO book (book_id, about, author_id, genre, subGenre, name, url, cover, likes, is_active, created_at, modified_at, read_global_time) VALUES (74, '"Apología de Sócrates" es un diálogo filosófico escrito por Platón que relata el discurso de defensa presentado por Sócrates durante su juicio en la Antigua Atenas. Sócrates, un influyente filósofo de la época, enfrenta acusaciones de corromper a la juventud y de no reconocer a los dioses reconocidos por la ciudad. A lo largo de la obra, Sócrates se defiende hábilmente, argumentando que su misión es buscar la verdad a través del diálogo y la reflexión, y que no tiene intenciones maliciosas.  La "Apología" revela la valentía y la integridad de Sócrates al enfrentar la muerte con serenidad, rechazando la opción de exiliarse y prefiriendo morir antes que renunciar a sus principios filosóficos. Este diálogo es una obra clave para entender la filosofía socrática y la actitud de Sócrates frente a la adversidad, proporcionando una visión profunda de su filosofía y su legado.', 9, 'Filosofia', 'Filosofia', 'Apologia de Socrates', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Libros%2FApologia%20de%20Socrates%2FApologia_de_Socrates-Platon.epub?alt=media&token=31a220ad-0b25-4688-8cff-cf026ed0f26f', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Libros%2FApologia%20de%20Socrates%2FApologia_de_Socrates-Platon.jpg?alt=media&token=47682810-70d5-4268-bff4-4cdc0807e193', 0, true, NOW(), NOW(), '00:00:00');
