INSERT INTO book (book_id, about, author_id, genre, subGenre, name, url, cover, likes, is_active, created_at, modified_at, read_global_time) VALUES (82, '"La carta robada" de Edgar Allan Poe es un relato de misterio que sigue al astuto detective Auguste Dupin mientras se enfrenta a un caso intrigante. El Ministro D— busca la recuperación de una carta comprometedora robada, pero la policía no puede hallarla. Dupin, con su brillantez y astucia, resuelve el enigma descubriendo que la carta no ha sido oculta en un lugar complicado, sino que ha sido escondida a simple vista. Poe juega con el suspense y la inteligencia en esta historia que establece el género del relato de detectives y deja una marca duradera en la literatura del misterio.', 2, 'Terror', 'Suspenso', 'La Carta Robada', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Libros%2FLa%20Carta%20Robada%2Fcarta%20robada%2C%20La%20-%20Edgar%20Allan%20Poe.epub?alt=media&token=04774fad-7288-42c2-9ffc-a125b6e7be59', 'https://firebasestorage.googleapis.com/v0/b/readmynow-87dfb.appspot.com/o/Libros%2FLa%20Carta%20Robada%2FLa%20carta%20robada.png?alt=media&token=cb591127-ea77-4bda-998b-68ad43b0a859', 0, true, NOW(), NOW(), '00:00:00');
